class UI{constructor(){this.cityInfo=document.getElementById("cityInfo"),this.cityInput=document.getElementById("searchCity")}showCityInfo(cityName){this.weatherIcon=cityName.weather[0].icon,this.countryCode=cityName.sys.country.toLowerCase(),this.timeZone=cityName.timezone/3600,this.cityInfo.innerHTML=`\n        <div class="card card-body mb-3">\n            <div class="row">\n                  <div class="col-md-3">\n                    <img class="img-thumbnail mb-2" src="http://openweathermap.org/img/wn/${this.weatherIcon}@4x.png">\n                    <h1><span class="badge badge-danger mt-4">${cityName.name}</span></h1>\n                  </div>\n                  <div class="col-md-9">\n                    <span class="badge badge-warning mt-1">Temperature: ${cityName.main.temp} Celsius</span>\n                    <span class="badge badge-info mt-1">Humidity: ${cityName.main.humidity} %</span>\n                    <span class="badge badge-success mt-1">Pressure: ${cityName.main.pressure} hPa</span>\n                    <span class="badge badge-primary mt-1">Wind Speed: ${cityName.wind.speed} m/s</span>\n                    <span></span>\n                    <br><br>\n                    <ul class="list-group">\n                        <li class="list-group-item">Latitude: ${cityName.coord.lat} Longitude: ${cityName.coord.lon}</li>\n                        <li class="list-group-item">Sky: ${cityName.weather[0].description}</li>\n                        <li class="list-group-item">Time Zone: ${this.timeZone}</li>\n                    </ul> \n                  </div>\n            </div>\n            <br><hr>\n            <h3 class="page-heading mb-3">Reference Country</h3>\n            <div id="news"><img class="img-thumbnail mb-2" src="https://www.countryflags.io/${this.countryCode}/flat/64.png"></div>\n        </div>\n        `}showAlert(message,cssClassName){this.clearAlert();const div=document.createElement("div");div.classList.add(cssClassName),this.cityInput.classList.add("is-invalid"),div.appendChild(document.createTextNode(message));const parentContainer=document.querySelector(".search"),refNode=document.getElementById("searchButton");parentContainer.insertBefore(div,refNode)}clearAlert(){const currentAlert=document.querySelector(".invalid-feedback");currentAlert&&currentAlert.remove()}clearFields(){this.clearAlert(),this.cityInput.classList.remove("is-invalid")}}